import React from 'react';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';

interface PageTransitionProps {
  to: string;
  children: React.ReactNode;
  className?: string;
}

const PageTransition: React.FC<PageTransitionProps> = ({
  to,
  children,
  className = ''
}) => {
  const navigate = useNavigate();

  const handleClick = (e: React.MouseEvent) => {
    e.preventDefault();
    
    // Create apocalyptic overlay
    const overlay = document.createElement('div');
    overlay.className = 'fixed inset-0 z-50 pointer-events-none';
    overlay.style.background = `
      radial-gradient(circle at center, rgba(255, 0, 0, 0.2) 0%, black 100%),
      repeating-linear-gradient(
        0deg,
        rgba(255, 0, 0, 0.1) 0px,
        rgba(255, 0, 0, 0.1) 1px,
        transparent 1px,
        transparent 2px
      )
    `;
    
    // Add warning text
    const warning = document.createElement('div');
    warning.className = 'fixed top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 text-center z-50 pointer-events-none';
    warning.innerHTML = `
      <div class="text-red-500 font-mono text-4xl font-bold mb-4 animate-pulse">SYSTEM BREACH DETECTED</div>
      <div class="text-red-400 font-mono text-xl">INITIATING EMERGENCY PROTOCOLS...</div>
    `;
    
    document.body.appendChild(overlay);
    document.body.appendChild(warning);

    // Add glitch effect to all text
    document.querySelectorAll('h1, h2, h3, p, span, button').forEach(el => {
      el.classList.add('animate-glitch');
    });

    // Create alarm sound
    const alarm = new Audio();
    alarm.src = 'data:audio/wav;base64,';
    alarm.play();

    // Navigate after effects
    setTimeout(() => {
      navigate(to);
      document.body.removeChild(overlay);
      document.body.removeChild(warning);
      document.querySelectorAll('h1, h2, h3, p, span, button').forEach(el => {
        el.classList.remove('animate-glitch');
      });
    }, 2000);
  };

  return (
    <motion.button
      onClick={handleClick}
      className={className}
      whileHover={{
        scale: 1.05,
        textShadow: "0 0 8px rgb(255, 0, 0)",
        boxShadow: "0 0 8px rgb(255, 0, 0)",
      }}
      whileTap={{ scale: 0.95 }}
    >
      {children}
    </motion.button>
  );
};

export default PageTransition;